import React, { Component } from 'react';

class RefSample extends Comment {
	input = React.createRef();

	handleFocus = () => {
		this.input.current.focus();
	}

	render() {
		return(
			<div>
				<input ref={this.input}/>
			</div>
		)
	}
}
